import { useState, useEffect, useCallback } from 'react';

const CardProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(
    useCallback(async () => {
      const url = 'http://localhost/projeto-react-php-bootstrap/backend/';

      const response = await fetch(url);

      const data = await response.json();

      setProducts(data);

      console.log(data);
    }),
    []
  );

  // id é categoria
  return (
    <>
      {products.map((product) => {
        return (
          <div class='box-produto col-3' id={product.categoria}>
            <img
              src={product.imagem}
              width='120px'
              onmouseover='aumentarImagem(this)'
              onmouseout='diminuirImagem(this)'
              alt={product.descricao}
            />
            <br />
            <p class='descricao'>{product.descricao}</p>
            <hr />
            <p class='descricao'>
              <strike>R$ {product.preco}</strike>
            </p>
            <br />
            <p class='preco'>R$ {product.preco_final}</p>
          </div>
        );
      })}
    </>
  );
};

export default CardProduct;
