import { useState, useEffect, useCallback } from 'react';

const CardProduct = () => {
  const [products, setProducts] = useState([]);

  useEffect(async () => {
    const url = 'http://localhost/projeto-react-php-bootstrap/backend/';

    const response = await fetch(url);

    const data = await response.json();

    return setProducts(data);
  }, []);

  // id é categoria
  return (
    <>
      <div className='row'>
        {products.map((product) => {
          return (
            <div
              class='box-produto col-3 d-flex-column justify-content-center align-items-center'
              id={product.categoria}
            >
              <img
                src={
                  'http://localhost/projeto-react-php-bootstrap/frontend/src/' +
                  product.imagem
                }
                width='120px'
                onmouseover='aumentarImagem(this)'
                onmouseout='diminuirImagem(this)'
                alt={product.descricao}
              />
              <br />
              <p class='descricao'>{product.descricao}</p>
              <hr />
              <p class='descricao'>
                <strike>R$ {product.preco}</strike>
              </p>
              <br />
              <p class='preco'>R$ {product.precofinal}</p>
            </div>
          );
        })}
      </div>
    </>
  );
};

export default CardProduct;
